import React from 'react';

import {Route} from 'react-router-dom';
import {createStructuredSelector} from 'reselect'
import {connect} from  'react-redux';

import CollectionsOverview from '../../components/collections-overview/collections-overview.component';

import CollectionPage from '../collection/collection.component';

  
import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions';
import {selectIsFetchingCollection, selectIsCollectionsLoaded} from  '../../redux/shop/shop.selectors';

import WithSpinner from '../../components/with-Spinner/with-Spinner.component';


const CollectionOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component{
     
  

    componentDidMount() {
        const {fetchCollectionsStartAsync} = this.props;
        fetchCollectionsStartAsync();
    }

render () {
    const {match, selectIsFetchingCollection, selectIsCollectionsLoaded} = this.props;
   
    
  return(<div className='shop-page'>
    
    < Route exact
     path={`${match.path}`}
     render={props =>(
     <CollectionOverviewWithSpinner 
     isLoading={selectIsFetchingCollection} 
     {...props}
      /> 
     )}/>
   
    <Route  
    path={`${match.path}/:collectionId`} 
    render={(props)=><CollectionPageWithSpinner isLoading={selectIsFetchingCollection} {...props} />}
    />
</div>
 )};
};

const mapStateToProps = createStructuredSelector({
    selectIsFetchingCollection: selectIsFetchingCollection,
    selectIsCollectionsLoaded: selectIsCollectionsLoaded
});

const mapDispatchToProps = dispatch =>  ({
  fetchCollectionsStartAsync: () => dispatch (fetchCollectionsStartAsync())
});

export default connect (
    mapStateToProps,
    mapDispatchToProps
    )(ShopPage);
